namespace Icinga\Module\Enrollment;

use Icinga\Authentication\Auth;
use Icinga\Module\Enrollment\Model\User;
use Icinga\Web\Url;
use ipl\Html\Html;
use ipl\Orm\Model;
use ipl\Web\Widget\Icon;

/**
 * Table widget to display a list of Users
 */
class UserTable extends DataTable
{
    protected $defaultAttributes = [
        'class'            => 'usage-table common-table',
        'data-base-target' => '_next'
    ];

    public function createColumns()
    {
        $columns = [];
        foreach ((new User())->getColumnDefinitions() as $column=>$options){

            if(is_array($options)) {
                $fieldtype = $options['fieldtype'] ?? "text";

                unset($options['fieldtype']);

                if ($fieldtype === "autocomplete" || $fieldtype === "text" || $fieldtype === "select") {
                    $columns[$column] = $options['label']??$column;

                }elseif ($fieldtype === "checkbox"){
                    $columns[$column."_text"] = [
                        'label'  => $options['label']??$column,
                        'column' => function ($data) use ($column) {
                            return $data->{$column}?("Yes"):t("No");
                        }
                    ];
                }elseif ($fieldtype === "localDateTime" ){
                    $columns[$column."_txt"] = [
                        'label'  => $options['label']??$column,
                        'column' => function ($data) use ($column) {
                            if($data->{$column}!=null){
                                return $data->{$column}->format("c");
                            }
                            return t("not set");
                        }
                    ];
                }
            }else{
                $columns[$column] = $options;
            }
        }
        $columns["status_text"] = [
            'label' => mt('enrollment', 'Status'),
            'column' => function ($data) {
                return $data;
            },
            'renderer' => function ($data) {
                return $data->getStatusAsText();
            }
        ];
        $columns["actions"] = [

            'label' => mt('enrollment', 'Actions'),
            'attributes' => ['class' => 'icon-col'],
            'column' => function ($data) {
                return $data;
            },
            'renderer' => function ($data) {
                $div = Html::tag("div", ['style' => 'white-space: nowrap;']);


                $icon = new Icon('envelope', ['title' => mt('enrollment', 'send mail') ]);
                $a = Html::tag("a", ['target' => '_next', 'style' => 'padding-right:1em; display:inline;', 'href' => Url::fromPath('enrollment/send/user', ['id' => $data->id])]);
                $a->add($icon);
                $div->add($a);

                $icon = new Icon('link', ['title' => mt('enrollment', 'copy link') ]);
                $a = Html::tag("a", ['target' => '_self', 'style' => 'padding-right:1em; display:inline;', 'href' => Url::fromPath('enrollment/register/user', ['secret' => $data->secret])]);
                $a->add($icon);
                $div->add($a);

                return $div;

            }
        ];


        return $columns;
    }


    protected function renderRow(Model $row)
    {
        $tr = parent::renderRow($row);

        if (Auth::getInstance()->hasPermission('enrollment/user/modify')) {
            $url = Url::fromPath('enrollment/user/edit', ['id' => $row->id]);

            $tr->getFirst("td")->getAttributes()->add(['href' => $url->getAbsoluteUrl(), 'data-icinga-modal' => true,
                'data-no-icinga-ajax' => true]);

        }

        return $tr;
    }
}
